// The prime factors of 13195 are 5, 7, 13 and 29.
//
// What is the largest prime factor of the number 600851475143 ?
// % op up through 10 in a for loop against the parameter to check where to start
// not giving correct answer for large nums, the 13195 does return the 29
function largestPrimeFactor(x){
    let output = [x];
    let remain;
    let base = 2;
    for(let i = base; i < 100; i++){
        if(x % i === 0){
            remain = x / i;
            output.push(i);
            output.push(remain);
            break;
        }
    }
    for(let j = base; j < remain; j++){
        if(remain % j === 0){
            remain /= j;
            output.push(j);
            output.push(remain);
            break;
        }
    }
    for(let k = base; k < remain; k++){
        if(remain % k === 0){
            remain /= k;
            output.push(k);
            output.push(remain);
            break;
        }
    }
    for(let l = base; l < remain; l++){
        if(remain % l === 0){
            remain /= l;
            output.push(l);
            output.push(remain);
            break;
        }
    }
    for(let m = base; m < remain; m++){
        if(remain % m === 0){
            remain /= m;
            output.push(m);
            output.push(remain);
            break;
        }
    }
    //return output;
    return output[output.length - 1];
}

